const ADD_POST = 'ADD-POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';
const ADD_MESSAGE = 'ADD-MESSAGE';
const UPDATE_NEW_DIALOG_ACTION_CREATER = 'UPDATE-NEW-DIALOG-ACTION-CREATER';

let store = {
    _state: {
        postsData: [
            { id: 1, name: 'Anar', text: 'Hi', likesCount: '0' },
            { id: 2, name: 'Anar', text: 'Vova', likesCount: '0' },
            { id: 3, name: 'Anar', text: '1337', likesCount: '0' },
        ],
        dialogsData: [
            { id: 1, name: 'Anar' },
            { id: 2, name: 'Vova' },
            { id: 3, name: 'Danya' },
        ],

        messagesData: [
            { id: 1, message: 'Hello' },
            { id: 2, message: 'How are you ?' },
            { id: 3, message: 'My name is Anar' },
        ],
        newTextPost: '',
    },
    getState() {
        return this._state;
    },

    rerenderEntireTree() {
        console.log('state changed')
    },
    subscribe(observer) {
        this.rerenderEntireTree = observer;
    },

    dispatch(action) {
        if (action.type === 'ADD-POST') {
            let newPost = {
                id: 4,
                name: 'Anar',
                text: this._state.newTextPost,
                likesCount: 0
            }
            this._state.postsData.push(newPost);
            this.rerenderEntireTree(this._state);
        } else if (action.type === 'UPDATE-NEW-POST-TEXT') {
            console.log(this._state);
            this._state.newTextPost = action.newText;
            this.rerenderEntireTree(this._state);
        } else if (action.type === 'ADD-MESSAGE') {
            let newMessage = {
                id: 4,
                message: this._state.newTextPost,
            }
            this._state.messagesData.push(newMessage);
            this.rerenderEntireTree(this._state);
        } else if (action.type === 'UPDATE-NEW-MESSAGE-ACTION-CREATER') {
            this._state.newTextPost = action.newText;
            this.rerenderEntireTree(this._state);
        }
    }
}

export let addPostActionCreater = () => {
    return {
        type: ADD_POST
    }
}

export let updateNewPostActionCreater = (text) => {
    return {
        type: UPDATE_NEW_POST_TEXT,
        newText: text
    }
}

export let updateNewDialogActionCreater = (text) => {
    return {
        type: UPDATE_NEW_MESSAGE_ACTION_CREATER,
        newText: text
    }
}

export let addDialogActionCreater = () => {
    return {
        type: ADD_MESSAGE
    }
}

window.store = store;

export default store;
