import React from 'react';
import axios from 'axios';


const Users = (props) => {

    let users = axios.get("https://social-network.samuraijs.com/api/1.0/users");
 

    let usersComponent = users.map((user) => {
        return <div key={user.id}>
            <span>
                <div>
                    <img src={user.photoUrl} />
                </div>
                <div>
                {user.followed
                            ? <button onClick={() => {
                                props.unFollow(user.id)
                            }}>Unfollow</button>
                            : <button onClick={() => {
                                props.follow(user.id)
                            }}>Follow</button>}
                </div>
            </span>
            <span>
                <div>{user.name}</div>
                <div>{user.status}</div>
                <div>{user.location}</div>
            </span>
        </div>
    });

    return <div>
        {usersComponent}
    </div>
}

export default Users;
