import React from 'react';
import Posts from './Posts';
import {addPostActionCreater, updateNewPostActionCreater} from '../../../reducers/posts-reducer';



const PostsContainer = (props) => {
    
    let state = props.store.getState();
    console.log(state);
    debugger;
    let onPostChange = () => {
        props.store.dispatch(updateNewPostActionCreater);
    }

    let addNewPost = () => {
        props.store.dispatch(addPostActionCreater);
    }


    return <Posts updateNewPostActionCreater={onPostChange} addPostActionCreater={addNewPost} postsData = {state.postsData} newTextPost = {state.newTextPost}/>
}

export default PostsContainer;
