import { setCurrentPageAC,setUsersTotalCountAC, followActionCreater, setUsersActionCreater, unfollowActionCreater } from '../../../reducers/users-reducer';
import Users from './Users';
import UsersClassComponent from './UsersClassComponent';
import {connect} from 'react-redux';




let mapStateToProps = (state) => {
    return {
        users: state.usersReducer.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage: state.usersPage.currentPage
    }
}

let mapDispatchToProps = (dispatch) => {
    return {
        follow: (userId) => {
            dispatch(followActionCreater(userId));
        },
        unFollow: (usersId) => {
            dispatch(unfollowActionCreater(usersId));
        },
        setUsers: (users) => {
            dispatch(setUsersActionCreater(users))
        },
        setTotalUsersCount: (totalCount) => {
            dispatch(setUsersTotalCountAC(totalCount))
        },
        setCurrentPage: (pageNumber) => {
            dispatch(setCurrentPageAC(pageNumber))
        },
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UsersClassComponent);
