let store = {
    _state: {
        postsData: [
            { id: 1, name: 'Anar', text: 'Hi', likesCount: '0' },
            { id: 2, name: 'Anar', text: 'Vova', likesCount: '0' },
            { id: 3, name: 'Anar', text: '1337', likesCount: '0' },
        ],
        newTextPost: '',
    },

    dialogsData: [
        { id: 1, name: 'Anar' },
        { id: 2, name: 'Vova' },
        { id: 3, name: 'Danya' },
    ],

    messagesData: [
        { id: 1, message: 'Hello' },
        { id: 2, message: 'How are you ?' },
        { id: 3, message: 'My name is Anar' },
    ],
    rerenderEntireTree() {
        console.log('state changed')
    },
    addPost() {
        let newPost = {
            id: 4,
            name: 'Anar',
            text: state.newTextPost,
            likesCount: 0
        }
        state.postsData.push(newPost);
        rerenderEntireTree(state);
    },
    changeNewText(newText) {
        state.newTextPost = newText;
        rerenderEntireTree(state);
    },
    subscribe(observer) {
        rerenderEntireTree = observer;
    }
}

window.store = store;

export default store;
