import React from 'react';
import Post from './Post/Post';
import styles from './posts.module.css';
import { rerenderEntireTree } from '../../../render';
import state from '../../../state';

const Posts = (props) => {

    let newPostElement = React.createRef();

    let addNewPost = () => {
        let text = newPostElement.current.value;
        props.addPost(text);
        rerenderEntireTree(state);
    }

    let postElements = props.postsData.map((item, index) => {
        return <Post key={index} name={item.name} id={item.id} text={item.text} likesCount={item.likesCount} />
    })

    return <div className={styles.blockPosts}>
        <p>Posts</p>
        <textarea ref={newPostElement} name="" id="" cols="30" rows="10"></textarea>
        <button onClick={addNewPost}>Add</button>
        {postElements}
    </div>
}

export default Posts;
